// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "../libs/PerpStructs.sol";

/**
 * @title IPerpMarket
 * @dev Interface for market-specific operations
 */
interface IPerpMarket {
    /**
     * @dev Adds a new market
     */
    function addMarket(
        string calldata symbol,
        bytes32 pythId,
        uint256 maxLeverage,
        uint256 liquidationThreshold,
        uint256 fee,
        uint256 fundingInterval,
        int256 maxFundingRate,
        uint256 maxPositionSize,
        uint256 maxOpenInterest,
        uint256 minInitialMargin
    ) external returns (uint32 marketIndex);

    /**
     * @dev Gets market information
     */
    function getMarket(uint32 marketIndex) external view returns (PerpStructs.Market memory);

    /**
     * @dev Gets market statistics
     */
    function getMarketStats(
        uint32 marketIndex
    )
        external
        view
        returns (
            uint256 longOpenInterest,
            uint256 shortOpenInterest,
            uint256 utilizationRate,
            int256 skewness,
            uint256 lastFundingTime,
            int256 currentFundingRate
        );

    /**
     * @dev Gets market utilization
     */
    function getMarketUtilization(uint32 marketIndex) external view returns (uint256 longUtil, uint256 shortUtil);

    /**
     * @dev Pauses a market
     */
    function pauseMarket(uint32 marketIndex) external;

    /**
     * @dev Unpauses a market
     */
    function unpauseMarket(uint32 marketIndex) external;

    /**
     * @dev Updates market funding rate
     */
    function updateFunding(uint32 marketIndex) external;

    /**
     * @dev Checks if market exists and is active
     */
    function isMarketActive(uint32 marketIndex) external view returns (bool);
}

