// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title IPerpOracle
 * @dev Interface for price oracle implementations
 */
interface IPerpOracle {
    /**
     * @dev Gets the latest price for a given market
     * @param marketId Identifier for the market
     * @return price Latest price with 18 decimals precision
     * @return timestamp Time when the price was updated
     * @return valid Whether the price is valid
     */
    function getPrice(bytes32 marketId) external view returns (uint256 price, uint256 timestamp, bool valid);

    /**
     * @dev Updates price feeds with latest data
     * @param priceUpdateData Encoded price update data from oracle
     */
    function updatePriceFeeds(bytes[] calldata priceUpdateData) external payable;

    /**
     * @dev Gets the fee required to update prices
     * @param priceUpdateData Encoded price update data
     * @return fee Amount required to pay for the update
     */
    function getUpdateFee(bytes[] calldata priceUpdateData) external view returns (uint256 fee);

    /**
     * @dev Checks if a price is not older than specified time
     * @param marketId Identifier for the market
     * @param maxAge Maximum acceptable age of the price in seconds
     * @return price Price data if within acceptable age
     */
    function getPriceNoOlderThan(bytes32 marketId, uint256 maxAge) external view returns (uint256 price);
}

