// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title PerpEvents
 * @dev Library containing all events used in the PerpEngine system
 */
library PerpEvents {
    /**
     * @dev Emitted when a new position is opened
     */
    event PositionOpened(
        address indexed trader,
        uint32 indexed marketIndex,
        uint256 size,
        uint256 margin,
        bool isLong,
        uint32 leverage,
        uint256 entryPrice,
        uint256 timestamp
    );

    /**
     * @dev Emitted when a position is closed
     */
    event PositionClosed(
        address indexed trader,
        uint32 indexed marketIndex,
        uint256 size,
        int256 pnl,
        uint256 exitPrice,
        uint256 timestamp
    );

    /**
     * @dev Emitted when a position is liquidated
     */
    event PositionLiquidated(
        address indexed trader,
        uint32 indexed marketIndex,
        uint256 size,
        address liquidator,
        int256 pnl,
        uint256 liquidationPrice
    );

    /**
     * @dev Emitted when a new market is added
     */
    event MarketAdded(
        uint32 indexed marketIndex,
        string symbol,
        address oracle,
        uint256 maxLeverage,
        uint256 maxPositionSize
    );

    /**
     * @dev Emitted when funding is updated for a market
     */
    event FundingUpdated(
        uint32 indexed marketIndex,
        int256 fundingRate,
        uint256 openInterestLong,
        uint256 openInterestShort
    );

    /**
     * @dev Emitted when emergency mode is enabled
     */
    event EmergencyModeEnabled(address indexed triggeredBy, uint256 timestamp);

    /**
     * @dev Emitted when a market's pause state changes
     */
    event MarketPaused(uint32 indexed marketIndex, bool isPaused);

    /**
     * @dev Emitted when insurance fund balance changes
     */
    event InsuranceFundUpdated(uint256 oldBalance, uint256 newBalance);

    /**
     * @dev Emitted when orderbook address is updated
     */
    event OrderBookUpdated(address indexed newOrderBook);

    /**
     * @dev Emitted when collateral is deposited
     */
    event CollateralDeposited(address indexed user, address indexed token, uint256 amount);

    /**
     * @dev Emitted when collateral is withdrawn
     */
    event CollateralWithdrawn(address indexed user, address indexed token, uint256 amount);

    /**
     * @dev Emitted when a new collateral token is added
     */
    event CollateralTokenAdded(address indexed token, uint256 decimals, uint256 collateralRatio);

    /**
     * @dev Emitted when oracle is added to a market
     */
    event OracleAdded(uint32 indexed marketIndex, address oracle, uint256 weight);
}

