// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./PerpStructs.sol";

/**
 * @title PerpMath
 * @dev Library containing all mathematical calculations for the PerpEngine system
 */
library PerpMath {
    // Constants
    uint256 private constant PRECISION = 1e18;
    uint256 private constant BASIS_POINTS = 10000;
    uint256 private constant PRICE_PRECISION = 1e10;

    /**
     * @dev Validates if a price is within acceptable slippage range
     */
    function isValidPrice(uint256 price, uint256 currentPrice, uint256 maxSlippage) internal pure returns (bool) {
        if (price == 0 || currentPrice == 0) return false;

        uint256 priceDiff = price > currentPrice ? price - currentPrice : currentPrice - price;

        uint256 slippage = (priceDiff * BASIS_POINTS) / currentPrice;
        return slippage <= maxSlippage;
    }

    /**
     * @dev Calculates the funding rate based on market imbalance
     */
    function calculateFundingRate(
        uint256 openInterestLong,
        uint256 openInterestShort,
        int256 maxFundingRate
    ) internal pure returns (int256) {
        if (openInterestShort == 0) return 0;

        // Calculate skew ratio between longs and shorts
        uint256 ratio = (openInterestLong * PRECISION) / openInterestShort;
        int256 skew = int256(ratio) - int256(PRECISION);

        // Calculate and cap funding rate
        int256 fundingRate = (skew * maxFundingRate) / int256(BASIS_POINTS);

        return
            fundingRate > maxFundingRate
                ? maxFundingRate
                : (fundingRate < -maxFundingRate ? -maxFundingRate : fundingRate);
    }

    /**
     * @dev Calculates PnL for a position
     */
    function calculatePnL(PerpStructs.Position memory position, uint256 currentPrice) internal pure returns (int256) {
        uint256 priceDelta;
        if (position.isLong) {
            if (currentPrice > position.entryPrice) {
                priceDelta = currentPrice - position.entryPrice;
                return int256((priceDelta * position.size) / position.entryPrice);
            } else {
                priceDelta = position.entryPrice - currentPrice;
                return -int256((priceDelta * position.size) / position.entryPrice);
            }
        } else {
            if (currentPrice > position.entryPrice) {
                priceDelta = currentPrice - position.entryPrice;
                return -int256((priceDelta * position.size) / position.entryPrice);
            } else {
                priceDelta = position.entryPrice - currentPrice;
                return int256((priceDelta * position.size) / position.entryPrice);
            }
        }
    }

    /**
     * @dev Calculates funding payment for a position
     */
    function calculateFundingPayment(
        PerpStructs.Position memory position,
        int256 marketCumulativeFunding
    ) internal pure returns (int256) {
        int256 fundingDelta = marketCumulativeFunding - position.lastCumulativeFunding;

        return
            position.isLong
                ? (-int256(position.size) * fundingDelta) / int256(PRECISION)
                : (int256(position.size) * fundingDelta) / int256(PRECISION);
    }

    /**
     * @dev Calculates liquidation price for a position
     */
    function calculateLiquidationPrice(
        PerpStructs.Position memory position,
        uint256 liquidationThreshold
    ) internal pure returns (uint256) {
        uint256 threshold = (position.margin * liquidationThreshold) / BASIS_POINTS;

        if (position.isLong) {
            return position.entryPrice - ((position.entryPrice * (position.margin - threshold)) / position.margin);
        } else {
            return position.entryPrice + ((position.entryPrice * (position.margin - threshold)) / position.margin);
        }
    }

    /**
     * @dev Calculates total value of a position including PnL and funding
     */
    function calculateTotalValue(
        uint256 margin,
        int256 unrealizedPnl,
        int256 fundingPayment
    ) internal pure returns (uint256) {
        int256 totalValue = int256(margin) + unrealizedPnl + fundingPayment;
        return totalValue <= 0 ? 0 : uint256(totalValue);
    }

    /**
     * @dev Calculates effective collateral value
     */
    function calculateEffectiveCollateral(uint256 nominal, uint256 collateralRatio) internal pure returns (uint256) {
        return (nominal * collateralRatio) / BASIS_POINTS;
    }

    /**
     * @dev Calculates fees for a position
     */
    function calculateFees(
        uint256 size,
        uint256 feeRate,
        uint256 protocolShare
    ) internal pure returns (uint256 tradingFee, uint256 protocolFee) {
        tradingFee = (size * feeRate) / BASIS_POINTS;
        protocolFee = (tradingFee * protocolShare) / BASIS_POINTS;
        return (tradingFee, protocolFee);
    }

    /**
     * @dev Calculates market utilization
     */
    function calculateUtilization(uint256 openInterest, uint256 maxOpenInterest) internal pure returns (uint256) {
        return maxOpenInterest == 0 ? 0 : (openInterest * PRECISION) / maxOpenInterest;
    }
}

