// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title PerpStructs
 * @dev Library containing all structs used in the PerpEngine system
 */
library PerpStructs {
    struct Position {
        bool isOpen;
        uint256 size; // Position size in USD (x10^18)
        uint256 margin; // Initial margin
        uint256 entryPrice; // Entry price in USD (x10^18)
        int256 lastCumulativeFunding; // Funding state at entry
        bool isLong; // Long or short position
        uint32 leverage; // Leverage as an integer
        uint32 marketIndex; // Index of the market
        uint256 lastUpdateTime; // Last time position was updated
        address collateralToken; // Collateral token address
    }

    struct OracleSource {
        address oracleAddress;
        bool isActive;
        uint256 weight; // Weight in basis points (total across all oracles should be 10000)
    }

    struct CollateralToken {
        IERC20 token;
        uint256 decimals;
        bool isActive;
        uint256 collateralRatio; // How much collateral value is recognized (in basis points)
    }

    struct Market {
        string symbol; // Market symbol (e.g., "BTC-USD")
        address oracle; // Oracle address
        uint256 maxLeverage; // Maximum allowed leverage
        uint256 liquidationThreshold; // % of margin to trigger liquidation
        uint256 fee; // Trading fee in bps
        uint256 openInterestLong; // Total long positions USD
        uint256 openInterestShort; // Total short positions USD
        int256 cumulativeFunding; // Accumulated funding
        uint256 lastFundingTime; // Last funding update
        uint256 fundingInterval; // Funding rate interval
        int256 maxFundingRate; // Max funding rate per interval
        uint256 maxPositionSize; // Max position size per user
        uint256 maxOpenInterest; // Max total open interest
        uint256 minInitialMargin; // Minimum initial margin
        bool isActive; // Market active status
    }

    struct FeeStructure {
        uint256 makerFee; // Fee for makers (limit orders)
        uint256 takerFee; // Fee for takers (market orders)
        uint256 liquidationFee; // Fee for liquidators
        uint256 protocolShare; // Protocol's share of fees
    }

    struct MarketStats {
        uint256 longOpenInterest;
        uint256 shortOpenInterest;
        uint256 utilizationRate;
        int256 skewness;
        uint256 lastFundingTime;
        int256 currentFundingRate;
    }

    struct PositionRisk {
        uint256 marginRatio;
        uint256 liquidationPrice;
        bool isLiquidatable;
        int256 unrealizedPnl;
        int256 fundingPayment;
    }

    struct CollateralValue {
        uint256 nominal;
        uint256 effective;
    }
}

contracts/perps/modules/PerpCollateralMo.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "../libs/PerpStructs.sol";
import "../libs/PerpEvents.sol";

/**
 * @title PerpCollateralModule
 * @dev Handles all collateral-related functionality for the perpetual trading system
 */
abstract contract PerpCollateralModule is Ownable, ReentrancyGuard {
    // Constants
    uint256 private constant BASIS_POINTS = 10000;

    // State variables
    uint256 public minMargin;
    address public feeRecipient;

    // Mappings
    mapping(address => bool) public supportedCollaterals;
    mapping(address => PerpStructs.CollateralToken) public collateralTokens;
    mapping(address => mapping(address => uint256)) public userCollateral;

    // Events
    event CollateralTokenAdded(address indexed token, uint256 decimals, uint256 collateralRatio);
    event CollateralDeposited(address indexed user, address indexed token, uint256 amount);
    event CollateralWithdrawn(address indexed user, address indexed token, uint256 amount);

    /**
     * @dev Initialize collateral module
     */
    function __PerpCollateralModule_init(address _feeRecipient, uint256 _minMargin) internal virtual onlyOwner {
        require(_feeRecipient != address(0), "Invalid fee recipient");
        require(_minMargin > 0, "Invalid min margin");

        feeRecipient = _feeRecipient;
        minMargin = _minMargin;
    }

    /**
     * @dev Add a new collateral token
     */
    function addCollateralToken(address token, uint256 decimals, uint256 collateralRatio) external onlyOwner {
        require(token != address(0), "Invalid token address");
        require(!supportedCollaterals[token], "Collateral already supported");
        require(collateralRatio <= BASIS_POINTS, "Invalid collateral ratio");

        supportedCollaterals[token] = true;
        collateralTokens[token] = PerpStructs.CollateralToken({
            token: IERC20(token),
            decimals: decimals,
            isActive: true,
            collateralRatio: collateralRatio
        });

        emit CollateralTokenAdded(token, decimals, collateralRatio);
    }

    /**
     * @dev Deposit collateral
     */
    function depositCollateral(address token, uint256 amount) external nonReentrant {
        require(supportedCollaterals[token], "Unsupported collateral");
        require(collateralTokens[token].isActive, "Collateral not active");

        PerpStructs.CollateralToken storage collateral = collateralTokens[token];
        require(collateral.token.transferFrom(msg.sender, address(this), amount), "Transfer failed");

        userCollateral[msg.sender][token] += amount;

        emit CollateralDeposited(msg.sender, token, amount);
    }

    /**
     * @dev Withdraw collateral
     */
    function withdrawCollateral(address token, uint256 amount) external nonReentrant {
        require(supportedCollaterals[token], "Unsupported collateral");
        require(userCollateral[msg.sender][token] >= amount, "Insufficient balance");

        // Check if withdrawal would make positions unsafe
        require(_isWithdrawalSafe(msg.sender, token, amount), "Unsafe withdrawal");

        userCollateral[msg.sender][token] -= amount;
        require(collateralTokens[token].token.transfer(msg.sender, amount), "Transfer failed");

        emit CollateralWithdrawn(msg.sender, token, amount);
    }

    /**
     * @dev Get collateral value
     */
    function getCollateralValue(
        address user,
        address token
    ) external view returns (uint256 nominal, uint256 effective) {
        nominal = userCollateral[user][token];
        effective = (nominal * collateralTokens[token].collateralRatio) / BASIS_POINTS;
    }

    /**
     * @dev Emergency withdraw
     */
    function emergencyWithdraw(address token) external nonReentrant {
        require(_isEmergencyMode(), "Emergency mode not active");
        uint256 amount = userCollateral[msg.sender][token];
        require(amount > 0, "No collateral to withdraw");

        userCollateral[msg.sender][token] = 0;
        require(collateralTokens[token].token.transfer(msg.sender, amount), "Transfer failed");

        emit CollateralWithdrawn(msg.sender, token, amount);
    }

    /**
     * @dev Check if withdrawal is safe
     */
    function _isWithdrawalSafe(
        address user,
        address token,
        uint256 withdrawalAmount
    ) internal view virtual returns (bool);

    /**
     * @dev Check if emergency mode is active
     */
    function _isEmergencyMode() internal view virtual returns (bool);

    /**
     * @dev Lock collateral for a position
     */
    function _lockCollateral(address user, address token, uint256 amount) internal virtual {
        require(userCollateral[user][token] >= amount, "Insufficient collateral");
        userCollateral[user][token] -= amount;
    }

    /**
     * @dev Release collateral from a position
     */
    function _releaseCollateral(address user, address token, uint256 amount) internal virtual {
        userCollateral[user][token] += amount;
    }

    /**
     * @dev Transfer protocol fees
     */
    function _transferProtocolFees(address user, address token, uint256 amount) internal virtual {
        userCollateral[user][token] -= amount;
        userCollateral[feeRecipient][token] += amount;
    }
}

contracts/perps/modules/PerpEmergencyMod.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "../libs/PerpEvents.sol";

/**
 * @title PerpEmergencyModule
 * @dev Handles emergency functionality for the perpetual trading system
 */
abstract contract PerpEmergencyModule is Ownable, ReentrancyGuard {
    // State
    bool public emergencyMode;
    uint256 public insuranceFundBalance;

    event InsuranceFundUpdated(uint256 oldBalance, uint256 newBalance);

    function __PerpEmergencyModule_init() internal virtual {
        emergencyMode = false;
        insuranceFundBalance = 0;
    }

    /**
     * @dev Enables emergency mode
     */
    function enableEmergencyMode() external onlyOwner {
        require(!emergencyMode, "Emergency mode already enabled");
        emergencyMode = true;
        emit PerpEvents.EmergencyModeEnabled(msg.sender, block.timestamp);
    }

    /**
     * @dev Disables emergency mode
     */
    function disableEmergencyMode() external onlyOwner {
        require(emergencyMode, "Emergency mode not enabled");
        emergencyMode = false;
    }

    /**
     * @dev Adds funds to insurance fund
     */
    function addToInsuranceFund() external payable {
        uint256 oldBalance = insuranceFundBalance;
        insuranceFundBalance += msg.value;
        emit InsuranceFundUpdated(oldBalance, insuranceFundBalance);
    }

    /**
     * @dev Withdraws funds from insurance fund
     */
    function withdrawFromInsuranceFund(uint256 amount) external onlyOwner {
        require(amount <= insuranceFundBalance, "Insufficient insurance fund balance");
        uint256 oldBalance = insuranceFundBalance;
        insuranceFundBalance -= amount;
        (bool success, ) = payable(owner()).call{ value: amount }("");
        require(success, "Transfer failed");
        emit InsuranceFundUpdated(oldBalance, insuranceFundBalance);
    }

    /**
     * @dev Checks if emergency mode is active
     */
    function isEmergencyMode() public view returns (bool) {
        return emergencyMode;
    }

    /**
     * @dev Gets insurance fund balance
     */
    function getInsuranceFundBalance() external view returns (uint256) {
        return insuranceFundBalance;
    }

    /**
     * @dev Virtual receive function to be implemented by child contracts
     */
    receive() external payable virtual {
        uint256 oldBalance = insuranceFundBalance;
        insuranceFundBalance += msg.value;
        emit InsuranceFundUpdated(oldBalance, insuranceFundBalance);
    }
}

contracts/perps/modules/PerpLiquidationM.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "../libs/PerpStructs.sol";
import "../libs/PerpEvents.sol";
import "../libs/PerpMath.sol";

/**
 * @title PerpLiquidationModule
 * @dev Handles liquidation-related functionality for the perpetual trading system
 */
abstract contract PerpLiquidationModule is ReentrancyGuard {
    using PerpMath for uint256;
    using PerpMath for PerpStructs.Position;

    // Constants
    uint256 private constant BASIS_POINTS = 10000;
    uint256 private constant LIQUIDATION_FEE = 500; // 5% in basis points

    function __PerpLiquidationModule_init() internal virtual {
        // Initialize module
    }

    // Abstract functions that must be implemented by the main contract
    function _getOraclePrice(uint32 marketIndex, bytes[] calldata priceUpdateData) internal virtual returns (uint256);

    function _updateFunding(uint32 marketIndex) internal virtual;

    function _releaseCollateral(address user, address token, uint256 amount) internal virtual;

    function _transferProtocolFees(address user, address token, uint256 amount) internal virtual;

    function _isEmergencyMode() internal view virtual returns (bool);

    function getPosition(address trader, uint32 marketIndex) public view virtual returns (PerpStructs.Position memory);

    function getMarket(uint32 marketIndex) public view virtual returns (PerpStructs.Market memory);

    /**
     * @dev Liquidates an underwater position
     */
    function liquidatePosition(
        address trader,
        uint32 marketIndex,
        bytes[] calldata priceUpdateData
    ) external virtual nonReentrant returns (uint256) {
        require(!_isEmergencyMode(), "System is in emergency mode");

        PerpStructs.Position memory position = getPosition(trader, marketIndex);
        require(position.isOpen, "Position not found");

        PerpStructs.Market memory market = getMarket(marketIndex);

        // Get current price and update funding
        uint256 currentPrice = _getOraclePrice(marketIndex, priceUpdateData);
        _updateFunding(marketIndex);

        // Calculate position value and check if liquidatable
        (uint256 remainingMargin, bool _isLiquidatable) = _calculateLiquidationDetails(position, market, currentPrice);
        require(_isLiquidatable, "Position not liquidatable");

        // Calculate liquidation rewards
        (uint256 liquidatorShare, uint256 protocolShare, uint256 returnAmount) = _calculateLiquidationShares(
            remainingMargin
        );

        // Handle transfers
        _handleLiquidationTransfers(
            trader,
            msg.sender,
            position.collateralToken,
            liquidatorShare,
            protocolShare,
            returnAmount
        );

        emit PerpEvents.PositionLiquidated(
            trader,
            marketIndex,
            position.size,
            msg.sender,
            -int256(position.margin - returnAmount),
            currentPrice
        );

        return liquidatorShare;
    }

    /**
     * @dev Calculates if a position is liquidatable
     */
    function isLiquidatable(address trader, uint32 marketIndex, uint256 currentPrice) external view returns (bool) {
        PerpStructs.Position memory position = this.getPosition(trader, marketIndex);
        if (!position.isOpen) return false;

        PerpStructs.Market memory market = getMarket(marketIndex);

        // Fixed tuple destructuring syntax
        (, bool _isLiquidatable) = _calculateLiquidationDetails(position, market, currentPrice);

        return _isLiquidatable;
    }

    /**
     * @dev Calculates liquidation details
     */
    function _calculateLiquidationDetails(
        PerpStructs.Position memory position,
        PerpStructs.Market memory market,
        uint256 currentPrice
    ) internal pure returns (uint256 remainingMargin, bool _isLiquidatable) {
        int256 unrealizedPnl = PerpMath.calculatePnL(position, currentPrice);

        if (unrealizedPnl >= 0) {
            remainingMargin = position.margin + uint256(unrealizedPnl);
        } else {
            uint256 loss = uint256(-unrealizedPnl);
            remainingMargin = loss < position.margin ? position.margin - loss : 0;
        }

        uint256 maintenanceMargin = (position.margin * market.liquidationThreshold) / BASIS_POINTS;
        _isLiquidatable = remainingMargin <= maintenanceMargin;

        return (remainingMargin, _isLiquidatable);
    }

    /**
     * @dev Calculates shares for liquidation participants
     */
    function _calculateLiquidationShares(
        uint256 remainingMargin
    ) internal pure returns (uint256 liquidatorShare, uint256 protocolShare, uint256 returnAmount) {
        uint256 liquidationFee = (remainingMargin * LIQUIDATION_FEE) / BASIS_POINTS;

        // Split liquidation fee between liquidator and protocol
        liquidatorShare = (liquidationFee * 8000) / BASIS_POINTS; // 80% to liquidator
        protocolShare = liquidationFee - liquidatorShare; // 20% to protocol

        // Remaining amount goes back to trader
        returnAmount = remainingMargin - liquidationFee;

        return (liquidatorShare, protocolShare, returnAmount);
    }

    /**
     * @dev Handles transfers during liquidation
     */
    function _handleLiquidationTransfers(
        address trader,
        address liquidator,
        address collateralToken,
        uint256 liquidatorShare,
        uint256 protocolShare,
        uint256 returnAmount
    ) internal {
        // Transfer liquidator share
        if (liquidatorShare > 0) {
            _releaseCollateral(liquidator, collateralToken, liquidatorShare);
        }

        // Transfer protocol share
        if (protocolShare > 0) {
            _transferProtocolFees(trader, collateralToken, protocolShare);
        }

        // Return remaining funds to trader
        if (returnAmount > 0) {
            _releaseCollateral(trader, collateralToken, returnAmount);
        }
    }
}

contracts/perps/modules/PerpMarketModule.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "../libs/PerpStructs.sol";
import "../libs/PerpEvents.sol";
import "../libs/PerpMath.sol";
import "../interfaces/IPerpMarket.sol";

/**
 * @title PerpMarketModule
 * @dev Handles market-related functionality for the perpetual trading system
 */
abstract contract PerpMarketModule is IPerpMarket, Ownable {
    using PerpMath for uint256;

    // Constants
    uint256 private constant BASIS_POINTS = 10000;
    uint256 private constant MAX_LEVERAGE = 100;
    uint256 private constant MIN_LIQUIDATION_THRESHOLD = 50;

    // State variables
    uint32 public marketCount;
    mapping(uint32 => PerpStructs.Market) public markets;
    mapping(uint32 => PerpStructs.FeeStructure) public marketFees;
    mapping(uint32 => bool) public marketPaused;
    mapping(uint32 => bytes32) public marketAggregatorIds;

    function __PerpMarketModule_init() internal virtual {
        marketCount = 0;
    }

    // Abstract functions that must be implemented by the main contract
    function _updateOraclePrice(uint32 marketIndex) internal virtual returns (uint256);

    function _isEmergencyMode() internal view virtual returns (bool);

    /**
     * @dev Adds a new market
     */
    function addMarket(
        string calldata symbol,
        bytes32 aggregatorId, // Changed from pythId to aggregatorId
        uint256 maxLeverage,
        uint256 liquidationThreshold,
        uint256 fee,
        uint256 fundingInterval,
        int256 maxFundingRate,
        uint256 maxPositionSize,
        uint256 maxOpenInterest,
        uint256 minInitialMargin
    ) external override onlyOwner returns (uint32) {
        require(aggregatorId != bytes32(0), "Invalid aggregator ID");
        require(maxLeverage > 0 && maxLeverage <= MAX_LEVERAGE, "Invalid max leverage");
        require(liquidationThreshold >= MIN_LIQUIDATION_THRESHOLD, "Invalid liquidation threshold");
        require(maxPositionSize > 0, "Invalid max position size");
        require(maxOpenInterest > 0, "Invalid max open interest");
        require(fundingInterval > 0, "Invalid funding interval");

        uint32 marketIndex = marketCount;
        marketCount += 1;

        marketAggregatorIds[marketIndex] = aggregatorId;

        markets[marketIndex] = PerpStructs.Market({
            symbol: symbol,
            oracle: address(0),
            maxLeverage: maxLeverage,
            liquidationThreshold: liquidationThreshold,
            fee: fee,
            openInterestLong: 0,
            openInterestShort: 0,
            cumulativeFunding: 0,
            lastFundingTime: block.timestamp,
            fundingInterval: fundingInterval,
            maxFundingRate: maxFundingRate,
            maxPositionSize: maxPositionSize,
            maxOpenInterest: maxOpenInterest,
            minInitialMargin: minInitialMargin,
            isActive: true
        });

        // Initialize fee structure
        marketFees[marketIndex] = PerpStructs.FeeStructure({
            makerFee: fee / 2, // Lower maker fees
            takerFee: fee,
            liquidationFee: 500, // 5% liquidation fee
            protocolShare: 2000 // 20% protocol share
        });

        emit PerpEvents.MarketAdded(marketIndex, symbol, address(0), maxLeverage, maxPositionSize);

        return marketIndex;
    }

    /**
     * @dev Internal function to update oracle price using Switchboard
     */
    function _updateOraclePrice(uint32 marketIndex, bytes[] calldata updates) internal returns (uint256) {
        require(marketExists(marketIndex), "Market does not exist");
        bytes32 aggregatorId = marketAggregatorIds[marketIndex];
        require(aggregatorId != bytes32(0), "No oracle configured");

        // This will be implemented by PerpEngine which has access to the OracleModule
        return _getOraclePrice(marketIndex, updates);
    }

    /**
     * @dev Abstract function to be implemented by PerpEngine
     */
    function _getOraclePrice(uint32 marketIndex, bytes[] calldata updates) internal virtual returns (uint256);

    /**
     * @dev Updates market funding rate
     */
    function updateFunding(uint32 marketIndex) public override {
        require(marketExists(marketIndex), "Market does not exist");
        PerpStructs.Market storage market = markets[marketIndex];

        if (block.timestamp < market.lastFundingTime + market.fundingInterval) {
            return;
        }

        uint256 periods = (block.timestamp - market.lastFundingTime) / market.fundingInterval;
        if (periods == 0) return;

        int256 fundingRate = PerpMath.calculateFundingRate(
            market.openInterestLong,
            market.openInterestShort,
            market.maxFundingRate
        );

        market.cumulativeFunding += fundingRate * int256(periods);
        market.lastFundingTime += periods * market.fundingInterval;

        emit PerpEvents.FundingUpdated(marketIndex, fundingRate, market.openInterestLong, market.openInterestShort);
    }

    /**
     * @dev Gets market information
     */
    function getMarket(uint32 marketIndex) public view virtual override returns (PerpStructs.Market memory) {
        require(marketExists(marketIndex), "Market does not exist");
        return markets[marketIndex];
    }

    /**
     * @dev Gets market statistics
     */
    function getMarketStats(
        uint32 marketIndex
    )
        external
        view
        override
        returns (
            uint256 longOpenInterest,
            uint256 shortOpenInterest,
            uint256 utilizationRate,
            int256 skewness,
            uint256 lastFundingTime,
            int256 currentFundingRate
        )
    {
        require(marketExists(marketIndex), "Market does not exist");
        PerpStructs.Market storage market = markets[marketIndex];

        longOpenInterest = market.openInterestLong;
        shortOpenInterest = market.openInterestShort;

        utilizationRate = PerpMath.calculateUtilization(
            market.openInterestLong + market.openInterestShort,
            market.maxOpenInterest
        );

        skewness = _calculateSkewness(market);
        lastFundingTime = market.lastFundingTime;
        currentFundingRate = _calculateCurrentFundingRate(market, skewness);
    }

    /**
     * @dev Gets market utilization
     */
    function getMarketUtilization(
        uint32 marketIndex
    ) external view override returns (uint256 longUtil, uint256 shortUtil) {
        require(marketExists(marketIndex), "Market does not exist");
        PerpStructs.Market storage market = markets[marketIndex];

        longUtil = PerpMath.calculateUtilization(market.openInterestLong, market.maxOpenInterest);

        shortUtil = PerpMath.calculateUtilization(market.openInterestShort, market.maxOpenInterest);
    }

    /**
     * @dev Pauses a market
     */
    function pauseMarket(uint32 marketIndex) external override onlyOwner {
        require(marketExists(marketIndex), "Market does not exist");
        require(!marketPaused[marketIndex], "Market already paused");

        marketPaused[marketIndex] = true;
        emit PerpEvents.MarketPaused(marketIndex, true);
    }

    /**
     * @dev Unpauses a market
     */
    function unpauseMarket(uint32 marketIndex) external override onlyOwner {
        require(marketExists(marketIndex), "Market does not exist");
        require(marketPaused[marketIndex], "Market not paused");

        marketPaused[marketIndex] = false;
        emit PerpEvents.MarketPaused(marketIndex, false);
    }

    /**
     * @dev Checks if market exists and is active
     */
    function isMarketActive(uint32 marketIndex) public view override returns (bool) {
        return
            marketExists(marketIndex) &&
            markets[marketIndex].isActive &&
            !marketPaused[marketIndex] &&
            !_isEmergencyMode();
    }

    /**
     * @dev Internal helper to check if market exists
     */
    function marketExists(uint32 marketIndex) internal view returns (bool) {
        return marketIndex < marketCount && markets[marketIndex].isActive;
    }

    /**
     * @dev Internal helper to calculate market skewness
     */
    function _calculateSkewness(PerpStructs.Market storage market) internal view returns (int256) {
        if (market.openInterestShort == 0) return 0;

        uint256 ratio = (market.openInterestLong * 1e18) / market.openInterestShort;
        return int256(ratio) - int256(1e18);
    }

    /**
     * @dev Internal helper to calculate current funding rate
     */
    function _calculateCurrentFundingRate(
        PerpStructs.Market storage market,
        int256 skewness
    ) internal view returns (int256) {
        if (block.timestamp < market.lastFundingTime + market.fundingInterval) {
            return 0;
        }

        int256 fundingRate = (skewness * market.maxFundingRate) / int256(BASIS_POINTS);

        return
            fundingRate > market.maxFundingRate
                ? market.maxFundingRate
                : (fundingRate < -market.maxFundingRate ? -market.maxFundingRate : fundingRate);
    }
}

