// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import { Structs } from "@switchboard-xyz/on-demand-solidity/Structs.sol";
import "../libs/PerpStructs.sol";
import { IPerpOracle } from "../interfaces/IPerpOracle.sol";

struct Update {
    bytes32 oracleId; // The publisher of the update
    int128 result; // The value of the recorded update
    uint256 timestamp; // The timestamp of the update
}

interface ISwitchboard {
    function latestUpdate(bytes32 aggregatorId) external view returns (Update memory);

    function updateFeeds(bytes[] calldata updates) external payable;

    function getFee(bytes[] calldata updates) external view returns (uint256);
}

/**
 * @title PerpOracleModule
 * @dev Handles oracle functionality using Switchboard
 */
abstract contract PerpOracleModule is IPerpOracle {
    // Constants
    uint256 private constant PRICE_PRECISION = 1e10;
    uint256 private constant STALENESS_PERIOD = 60;

    // State
    ISwitchboard public switchboard;

    // Market oracle configurations
    struct MarketOracle {
        bytes32 aggregatorId;
        bool isActive;
    }

    // Error definitions
    error InsufficientFee(uint256 expected, uint256 received);
    error InvalidResult(int128 result);
    error StalePrice(uint256 timestamp);
    error NegativePrice(int128 price);

    // Events
    event OracleUpdated(uint32 marketIndex, bytes32 aggregatorId);
    event PriceUpdated(uint32 marketIndex, int128 price, uint256 timestamp);

    mapping(uint32 => MarketOracle) public marketOracles;

    /**
     * @dev Initializes the oracle module
     */
    function __PerpOracleModule_init(address _switchboardContract) internal virtual {
        require(_switchboardContract != address(0), "Invalid Switchboard address");
        switchboard = ISwitchboard(_switchboardContract);
    }

    /**
     * @dev Sets oracle configuration for a market
     */
    function setMarketOracle(uint32 marketIndex, bytes32 aggregatorId) external virtual {
        require(aggregatorId != bytes32(0), "Invalid aggregator ID");

        marketOracles[marketIndex] = MarketOracle({ aggregatorId: aggregatorId, isActive: true });

        emit OracleUpdated(marketIndex, aggregatorId);
    }

    /**
     * @dev Gets price from configured oracle with updates
     */
    function _getOraclePrice(uint32 marketIndex, bytes[] calldata updates) internal virtual returns (uint256) {
        MarketOracle storage oracle = marketOracles[marketIndex];
        require(oracle.isActive, "Oracle not configured");

        // Get the fee for updating the feeds
        uint256 fee = switchboard.getFee(updates);
        if (msg.value < fee) {
            revert InsufficientFee(fee, msg.value);
        }

        // Submit the updates to the Switchboard contract
        switchboard.updateFeeds{ value: fee }(updates);

        // Read the current value
        Update memory latestUpdate = switchboard.latestUpdate(oracle.aggregatorId);

        // Check staleness
        if (block.timestamp - latestUpdate.timestamp > STALENESS_PERIOD) {
            revert StalePrice(latestUpdate.timestamp);
        }

        // Validate result
        if (latestUpdate.result <= 0) {
            revert InvalidResult(latestUpdate.result);
        }

        emit PriceUpdated(marketIndex, latestUpdate.result, latestUpdate.timestamp);

        // Safe conversion: we've already checked result > 0
        return _normalizePrice(uint128(latestUpdate.result));
    }

    /**
     * @dev Normalizes price to contract's precision
     */
    function _normalizePrice(uint128 price) internal pure returns (uint256) {
        // Switchboard returns prices with 18 decimals
        return (uint256(price) * PRICE_PRECISION) / 1e18;
    }

    /**
     * @dev View function to get latest price without updates
     */
    function getLatestPrice(uint32 marketIndex) external view returns (uint256) {
        MarketOracle storage oracle = marketOracles[marketIndex];
        require(oracle.isActive, "Oracle not configured");

        // Get the latest update as struct
        Update memory update = switchboard.latestUpdate(oracle.aggregatorId);

        require(block.timestamp - update.timestamp <= STALENESS_PERIOD, "Stale price");
        if (update.result <= 0) {
            revert InvalidResult(update.result);
        }

        // Safe conversion: we've already checked result > 0
        return _normalizePrice(uint128(update.result));
    }
}

